mapper.select:
SELECT cs.name name, cs.id id, o.id entityId, o.pId pId, o.isLeaf isLeaf, o.ordr ordr
FROM TstCaseInTask o JOIN TstCase cs ON o.caseId = cs.id
WHERE o.taskId=#{taskId}
AND o.projectId=#{projectId}
AND o.deleted != true AND o.disabled != true ORDER BY o.pId, o.ordr

SELECT cs.id id, cs.name name,
  o.id entityId, o.pId pId, o.isLeaf isLeaf, o.ordr ordr

FROM TstCaseInTask o
JOIN TstCase cs ON o.caseId = cs.id

WHERE o.id=#{id}

    AND o.projectId=#{projectId}

SELECT cs.id id, cs.*, o.id entityId, o.id entityId, o.pId pId, o.isLeaf isLeaf, o.status status, o.result result, o.ordr ordr, o.planId planId, o.taskId taskId
FROM TstCaseInTask o JOIN TstCase cs ON o.caseId = cs.id
WHERE o.id=#{id}
AND o.projectId=#{projectId}
