mapper.select:
SELECT CONCAT(o.id,'-',o.title ) name, 'issue' type FROM IsuIssue o

WHERE o.id = #{text}
AND o.projectId = #{projectId}

    AND o.id NOT IN

        #{item}


ORDER BY o.id ASC

SELECT o.id, CONCAT(o.id,'-',o.title ) name, 'issue' type FROM IsuIssue o

WHERE o.title LIKE CONCAT('%','${text}','%' )
AND o.projectId = #{projectId}

    AND o.id NOT IN

        #{item}


ORDER BY o.id ASC
