mapper.select:
SELECT * FROM TstOrg o
JOIN TstOrgUserRelation r on r.orgId=o.id
WHERE r.userId = #{userId}

    AND o.name LIKE CONCAT('%','${keywords}','%' )


    AND o.disabled = #{disabled}

AND o.deleted != TRUE

mapper.update:
UPDATE TstUser
SET defaultOrgId=#{orgId}, defaultOrgName=#{orgName}
WHERE id=#{id}

mapper.delete:
UPDATE TstOrg
SET deleted=TRUE
WHERE id=#{id}
