mapper.select:
SELECT * FROM TstCustomField o

WHERE o.id = #{id}

    AND o.orgId=#{orgId}

SELECT * FROM TstCustomField o
WHERE o.id = #{id}

    AND o.orgId=#{orgId}

mapper.update:
UPDATE TstCustomField SET label=#{label},descr=#{descr},applyTo=#{applyTo},type=#{type},rows=#{rows}, format=#{format},required=#{required},global=#{global},
orgId=#{orgId},`global`=#{global},required=#{required}, disabled=#{disabled}, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}

mapper.delete:
UPDATE TstCustomField
SET deleted=TRUE, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}

mapper.update:
UPDATE TstCustomField
SET ordr=#{ordr}, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}
