mapper.select:
SELECT o.*, usr.nickname userName, prj.name projectName, ver.name verName FROM TstPlan o
JOIN TstUser usr ON usr.id=o.userId JOIN TstProject prj ON prj.id=o.projectId LEFT JOIN TstVer ver ON ver.id=o.verId
WHERE o.projectId = #{projectId}
AND o.name LIKE CONCAT('%','${keywords}','%' )
AND o.status = #{status}
AND o.deleted != TRUE ORDER BY o.createTime DESC

SELECT o.*, usr.nickname userName, prj.name projectName, ver.name verName FROM TstPlan o
JOIN TstUser usr ON usr.id=o.userId JOIN TstProject prj ON prj.id=o.projectId LEFT JOIN TstVer ver ON ver.id=o.verId
WHERE o.id = #{id}

mapper.update:
UPDATE TstPlan
SET name=#{name}, estimate=#{estimate}, startTime=#{startTime}, endTime=#{endTime},
descr=#{descr}, projectId=#{projectId}, userId=#{userId}, verId=#{verId},
disabled=#{disabled}

WHERE id=#{id}

UPDATE TstPlan
SET deleted = TRUE
WHERE id=#{id}
