mapper.select:
SELECT o.*, usr.nickname userName, prj.name projectName, casePrj.name caseProjectName FROM TstSuite o
JOIN TstUser usr ON usr.id=o.userId JOIN TstProject prj ON prj.id=o.projectId JOIN TstProject casePrj ON casePrj.id=o.caseProjectId
WHERE o.projectId = #{projectId}
AND o.name LIKE CONCAT('%','${keywords}','%' )
AND o.disabled = #{disabled}
AND o.deleted != TRUE ORDER BY o.id ASC

SELECT o.*, usr.nickname userName, prj.name projectName, casePrj.name caseProjectName, ( SELECT count(cs.id) FROM TstCaseInSuite cs WHERE cs.suiteId = o.id AND isLeaf=TRUE AND cs.deleted != true AND cs.disabled != true ) `count`
FROM TstSuite o
JOIN TstUser usr ON usr.id=o.userId JOIN TstProject prj ON prj.id=o.projectId JOIN TstProject casePrj ON casePrj.id=o.caseProjectId
WHERE o.id = #{id}
AND o.projectId=#{projectId}

mapper.update:
UPDATE TstSuite
SET deleted = TRUE
WHERE id=#{id} AND projectId=#{projectId}
