mapper.select:
SELECT usr.*, s.leftSizeDesign leftSizeDesign, s.leftSizeExe leftSizeExe, s.issueView issueView, s.issueColumns issueColumns
FROM TstUser usr JOIN TstUserSettings s ON s.userId = usr.id
WHERE usr.token = #{token} AND usr.disabled != TRUE AND usr.deleted != TRUE LIMIT 1;

SELECT * FROM TstUser WHERE phone = #{phone}
AND disabled != TRUE AND deleted != TRUE
LIMIT 1;

SELECT salt FROM TstUser WHERE id = #{id}
AND disabled != TRUE AND deleted != TRUE
LIMIT 1;

mapper.update:
UPDATE TstUser
SET email=#{email}, nickname=#{nickname}, phone=#{phone}, disabled=#{disabled}
WHERE id=#{id}

UPDATE TstUser
SET ${prop} = #{value}, updateTime=NOW()
WHERE id=#{id}

UPDATE TstUserSettings
SET issueView=#{issueView}
WHERE userId=#{id}

UPDATE TstUserSettings
SET issueColumns=#{issueColumns}
WHERE userId=#{userId}

UPDATE TstUserSettings
SET issueFields=#{issueFields}
WHERE userId=#{userId}
