mapper.select:
SELECT usr.*, s."leftSizeDesign", s."leftSizeExe", s."issueView", s."issueColumns"
FROM "TstUser" usr JOIN "TstUserSettings" s ON s."userId" = usr.id
WHERE usr.token = #{token} AND NOT usr.disabled AND NOT usr.deleted LIMIT 1;

SELECT *
FROM "TstUser"
WHERE phone = #{phone}
  AND NOT disabled
  AND NOT deleted
LIMIT 1;

SELECT salt
FROM "TstUser"
WHERE id = #{id}
  AND NOT disabled
  AND NOT deleted
LIMIT 1;

mapper.update:
UPDATE "TstUser"
SET email = #{email},
    nickname = #{nickname},
    phone = #{phone},
    disabled = #{disabled}
WHERE id = #{id}
