mapper.select:
SELECT * FROM TstUser WHERE email = #{email}
AND disabled != TRUE AND deleted != TRUE
LIMIT 1;

SELECT * FROM TstUser WHERE token = #{token}
AND disabled != TRUE AND deleted != TRUE
LIMIT 1;

SELECT * FROM TstUser WHERE phone = #{phone}
AND disabled != TRUE AND deleted != TRUE
LIMIT 1;

SELECT salt FROM TstUser WHERE id = #{id}
AND disabled != TRUE AND deleted != TRUE
LIMIT 1;

mapper.update:
UPDATE TstUser
SET email=#{email}, nickname=#{nickname}, phone=#{phone}, disabled=#{disabled}
WHERE id=#{id}

UPDATE TstUser
SET ${prop} = #{value}, updateTime=NOW()
WHERE id=#{id}

UPDATE TstUser
SET defaultOrgId=#{orgId}, defaultOrgName=#{orgName}
WHERE id=#{id}

UPDATE TstUser
SET defaultPrjId=#{prjId}, defaultPrjName=#{prjName}
WHERE id=#{id}

UPDATE TstUser
SET leftSizeDesign=#{leftSizeDesign}, leftSizeExe=#{leftSizeExe}
WHERE id=#{id}
